"use strict";(self.webpackChunk_0420_syj_github_io=self.webpackChunk_0420_syj_github_io||[]).push([[1909],{6627:n=>{n.exports=JSON.parse('{"blogPosts":[{"id":"2023-05-31-turnover","metadata":{"permalink":"/daily/2023-05-31-turnover","source":"@site/daily/2023-05-31-turnover/index.md","title":"\uccab \uc774\uc9c1, 2\uc8fc \ud6c4\uae30 \u2b50","description":"\uccab \uc774\uc9c1\uc744 \ud558\uace0 2\uc8fc\uac00 \uc9c0\ub0ac\ub2e4. \uc774\uc9c1\uc744 \ud558\uba74\uc11c \ub290\ub080 \uc810\ub4e4\uc744 \uc815\ub9ac\ud574\ubcf8\ub2e4.","date":"2023-05-31T00:00:00.000Z","formattedDate":"2023\ub144 5\uc6d4 31\uc77c","tags":[{"label":"career","permalink":"/daily/tags/career"}],"readingTime":2.395,"hasTruncateMarker":true,"authors":[{"name":"\uc2ec\uc644","title":"\ud504\ub860\ud2b8\uc5d4\ub4dc \uac1c\ubc1c\uc790 @ HandHug","url":"https://github.com/0420syj","imageURL":"https://avatars.githubusercontent.com/u/22449484","key":"wannysim"}],"frontMatter":{"slug":"2023-05-31-turnover","title":"\uccab \uc774\uc9c1, 2\uc8fc \ud6c4\uae30 \u2b50","description":"\uccab \uc774\uc9c1\uc744 \ud558\uace0 2\uc8fc\uac00 \uc9c0\ub0ac\ub2e4. \uc774\uc9c1\uc744 \ud558\uba74\uc11c \ub290\ub080 \uc810\ub4e4\uc744 \uc815\ub9ac\ud574\ubcf8\ub2e4.","image":"./images/1.png","authors":["wannysim"],"tags":["career"],"enableComments":true},"nextItem":{"title":"\uc624\uc0ac\uce74\uc5d0\uc11c \uc640\uc778 \uad6c\uc785\ud558\uae30 \ud83c\udf77","permalink":"/daily/2023-05-11-japan-wine"}},"content":"\uccab \uc774\uc9c1\uc744 \ud558\uace0 2\uc8fc\uac00 \uc9c0\ub0ac\ub2e4. \uc774\uc9c1\uc744 \ud558\uba74\uc11c \ub290\ub080 \uc810\ub4e4\uc744 \uc815\ub9ac\ud574\ubcf8\ub2e4.\\n\\n\x3c!-- truncate --\x3e\\n\\n## \uac04\ub2e8\ud558\uac8c \uc801\uc5b4\ubcf4\ub294 \uc774\uc9c1 \ud6c4\uae30\\n\\n\uc0dd\uac01\ubcf4\ub2e4 \uae08\ubc29 \uc0c8\ub85c\uc6b4 \uc5c5\ubb34\uc5d0 \uc801\uc751\ud560 \uc218 \uc788\uc5c8\ub2e4. \uc774\uc804 \uc9c1\uc7a5 \ud504\ub85c\uc81d\ud2b8 \ud06c\uae30\uc5d0 \ube44\ud574 \uc791\uae30\ub3c4 \ud558\uace0, \ubc30\uc6e0\ub358 \uac83\ub4e4\uc774 \uc54c\uac8c \ubaa8\ub974\uac8c \ub3c4\uc6c0\uc774 \ub9ce\uc774 \ub418\uace0\uc788\ub2e4.\\n\\n### \uc5c5\ubb34\uc77c\uc9c0 \uc801\uae30\\n\\n\uac1c\uc778\uc801\uc73c\ub85c \uc5c5\ubb34\uc77c\uc9c0\ub97c \uac04\ub2e8\ud558\uac8c \uc801\ub294\uac8c \ud6a8\uc728\uc801\uc73c\ub85c \uc77c\ud558\ub294\ub370 \ub3c4\uc6c0\uc774 \ub9ce\uc774 \ub418\uace0 \uc788\ub2e4. Notion\uc744 \uc0ac\uc6a9\ud574\uc11c \uc801\uace0 \uc788\ub294\ub370 \ub300\ucda9 \uc544\ub798\uc640 \uac19\uc740 \uc591\uc2dd\uc73c\ub85c \uc801\uace0\uc788\ub2e4.\\n\\n[![Notion Format](./images/1.png)](./images/1.png)\\n\\n### \ub9e5\ubd81\uc740 \uc0dd\uac01\ubcf4\ub2e4 \ud3b8\ud558\ub2e4\\n\\n2\ub144\uc804 \uc778\ud134\ub54c \ucc98\uc74c \ub9e5\ubd81\uc744 2\uac1c\uc6d4 \uac00\ub7c9 \uc37c\uc5c8\ub2e4. \uc9c0\uae08 \uc0dd\uac01\ud574\ubcf4\uba74 \uc815\uaddc\uc9c1 \uc804\ud658\uc774 \uac78\ub9b0 \uc778\ud134\ub54c \ud55c \ubc88\ub3c4 \uc548 \uc368\ubcf8 \ub9e5\ubd81\uc744 \uc4f0\ub294 \uc815\uc2e0\ub098\uac04 \uacb0\uc815\uc744 \ud55c\uac8c \uc774\ud574\ub294 \uc548 \ub41c\ub2e4.\\n\\n\uc778\ud134 \uc598\uae30\ub97c \uac11\uc790\uae30 \ud558\ub294\uac74, \uadf8\ub9cc\ud07c \ucc98\uc74c \uc4f0\ub294\ub370\ub3c4 \uc27d\uac8c \uc801\uc751\ud560 \uc218 \uc788\uc5c8\ub2e4. \uadf8\ub7ec\ub098 \uc9d1\uc5d0\uc11c\ub294 \uc708\ub3c4\uc6b0 \ud658\uacbd\uc5d0\uc11c \uac1c\ubc1c\uc744 \ud588\uae30 \ub54c\ubb38\uc5d0 \uae08\uc0c8 \ub9e5\ubd81\uc740 \uc78a\ud600\uc84c\ub2e4. \uc774 \uacf3\uc5d0\uc11c\ub294 \ub9e5\ubd81\uc744 \uc0ac\uc6a9\ud558\uace0 \uc788\uace0, \uc0dd\uac01\ubcf4\ub2e4 \ud3b8\ud558\ub2e4.\\n\\n\uac1c\uc778\uc801\uc73c\ub85c \uc4f0\ub294 \uadf8\ub7a8\ub3c4 \ucd94\ud6c4\uc5d0 \ub9e5\ubd81\uc73c\ub85c \ubc14\uafc0 \uc0dd\uac01\uc774\ub2e4.\\n\\n### \ud1b5\uadfc\uc2dc\uac04\uc740 30\ubd84 \ubc11\uc73c\ub85c\\n\\n\ubc84\uc2a4\ub85c 30\ubd84\uc774 \uc548 \uac78\ub9ac\ub358 \ud1b5\uadfc\uc2dc\uac04\uc774\\n\uc9c0\ud558\ucca0 1\uc2dc\uac04\uc73c\ub85c \ub298\uc5b4\ub0ac\ub2e4. \uac8c\ub2e4\uac00 9\ud638\uc120 \uae09\ud589\uc744 \ud0c4\ub2e4.\\n\\n\ud1b5\uadfc\uc2dc\uac04\uc774 \uae38\uc5b4\uc9c0\ub2c8\uae4c \uae08\ubc29 \ud53c\ub85c\ud574\uc9c4\ub2e4.\\n\\n\uadf8\ub798\uc11c \uc5c5\ubb34 \uc911 \uc26c\ub294 \uc2dc\uac04 \ub8e8\ud2f4\uc744 \ub9cc\ub4e4\uae30 \uc704\ud574 \ub178\ub825\uc911\uc774\ub2e4. 40\ubd84(\uc9d1\uc911) + 5\ubd84(\ud734\uc2dd)\uc744 \uc0dd\uac01\ud558\uace0 \uc788\uc9c0\ub9cc, \uc544\uc9c1 \uc870\uc728\uc774 \ub35c \ub410\ub2e4.\\n\\n\ucc28\ub77c\ub9ac \uc6b4\ub3d9\uc744 \ub2e4\uc2dc \uc2dc\uc791\ud574\uc57c\ub418\ub098 \uc2f6\uae30\ub3c4 \ud558\ub2e4 \ud83d\ude02"},{"id":"2023-05-11-japan-wine","metadata":{"permalink":"/daily/2023-05-11-japan-wine","source":"@site/daily/2023-05-11-japan-wine/index.md","title":"\uc624\uc0ac\uce74\uc5d0\uc11c \uc640\uc778 \uad6c\uc785\ud558\uae30 \ud83c\udf77","description":"\uc624\uc0ac\uce74 \uc5ec\ud589 \uc911 \uc640\uc778\uc744 \uad6c\uc785\ud558\uace0 \uc2f6\ub2e4\uba74?","date":"2023-05-11T00:00:00.000Z","formattedDate":"2023\ub144 5\uc6d4 11\uc77c","tags":[{"label":"travel","permalink":"/daily/tags/travel"},{"label":"japan","permalink":"/daily/tags/japan"},{"label":"osaka","permalink":"/daily/tags/osaka"},{"label":"wine","permalink":"/daily/tags/wine"}],"readingTime":5.09,"hasTruncateMarker":true,"authors":[{"name":"\uc2ec\uc644","title":"\ud504\ub860\ud2b8\uc5d4\ub4dc \uac1c\ubc1c\uc790 @ HandHug","url":"https://github.com/0420syj","imageURL":"https://avatars.githubusercontent.com/u/22449484","key":"wannysim"}],"frontMatter":{"slug":"2023-05-11-japan-wine","title":"\uc624\uc0ac\uce74\uc5d0\uc11c \uc640\uc778 \uad6c\uc785\ud558\uae30 \ud83c\udf77","description":"\uc624\uc0ac\uce74 \uc5ec\ud589 \uc911 \uc640\uc778\uc744 \uad6c\uc785\ud558\uace0 \uc2f6\ub2e4\uba74?","image":"./images/4.jpg","authors":["wannysim"],"tags":["travel","japan","osaka","wine"],"enableComments":true},"prevItem":{"title":"\uccab \uc774\uc9c1, 2\uc8fc \ud6c4\uae30 \u2b50","permalink":"/daily/2023-05-31-turnover"},"nextItem":{"title":"230424 \uc0dd\uac01\uc815\ub9ac \ud83d\ude44","permalink":"/daily/2023-04-24-daily"}},"content":"\uc624\uc0ac\uce74 \uc5ec\ud589 \uc911 \uc640\uc778\uc744 \uad6c\uc785\ud558\uae30 \uc704\ud574 \uc870\uc0ac\ud588\ub358 \ub0b4\uc6a9\uc744 \uc815\ub9ac\ud574\ubcf8\ub2e4.\\n\\n\x3c!-- truncate --\x3e\\n\\n## FYI \ud83d\udccc\\n\\n\uc77c\ubcf8\uc740 \uc544\uc2dc\uc544\uc5d0\uc11c \uc640\uc778\uc744 \uad6c\uc785\ud558\uae30\uc5d0\ub294 \uac00\uc7a5 \uc88b\uc740 \ub098\ub77c \uc911 \ud558\ub098\ub2e4. \uc5f0\uac04 1\uc778\ub2f9 \uc640\uc778 \uc18c\ube44\ub7c9\uc774 3.5L\ub85c \uc544\uc2dc\uc544 1\uc704, \uc138\uacc4 19\uc704\ub2e4. (2020\ub144 \uae30\uc900)\\n\\n\uadf8\ub798\uc11c \ud55c\uad6d\uc5d0\uc11c\ub294 \uc9c1\uad6c\ub85c \ubc16\uc5d0 \uad6c\ud560 \uc218 \uc5c6\ub294 \uc640\uc778\uc774\ub098, \ud130\ubb34\ub2c8\uc5c6\uc774 \ube44\uc2fc \uc640\uc778\uc744 \ud569\ub9ac\uc801\uc73c\ub85c \uad6c\uc785\ud560 \uc218 \uc788\ub2e4 \ud83d\ude0a\\n\\n## \uc5b4\ub514\uc11c \uc5b4\ub5bb\uac8c \uad6c\uc785\ud560\uae4c \ud83e\udd14\\n\\n### \uc628\ub77c\uc778 \uad6c\uc785\\n\\n\uc77c\ubcf8\uc740 \uc628\ub77c\uc778\uc73c\ub85c \uc640\uc778\uc744 \uad6c\uc785\ud560 \uc218 \uc788\ub2e4. \uadf8\ub798\uc11c \ubc14\uc05c \ubd84\ub4e4\uc740 \uc628\ub77c\uc778\uc5d0\uc11c \uad6c\uc785\ud558\uace0, \ud638\ud154 \uce21\uc5d0 \ubbf8\ub9ac \uc5f0\ub77d\ud574 \ubc30\uc1a1\ubc1b\ub294\ub2e4\uace0 \ud55c\ub2e4.\\n\\n:::info \uc8fc\uc694 \uc628\ub77c\uc778 \uc1fc\ud551\ubab0\\n\\n- [\ub77c\ucfe0\ud150](https://www.rakuten.co.jp/category/wine/) : \uc77c\ubcf8 \ucd5c\ub300 \uaddc\ubaa8\uc758 \uc628\ub77c\uc778 \uc1fc\ud551\ubab0\\n- [ELEVIN](https://www.elevin.jp/) : \uc640\uc778 \uc804\ubb38 \uc628\ub77c\uc778 \uc1fc\ud551\ubab0. \ub77c\ucfe0\ud150\uc5d0\ub3c4 \uc785\uc810\ud574\uc788\uc9c0\ub9cc, \uacf5\uc2dd \uc0ac\uc774\ud2b8\uac00 \ubb3c\uac74\uc774 \ub354 \ub9ce\uc74c. [\uc774\uc6a9\ubc29\ubc95](https://cafe.naver.com/winerack24/223163)\\n\\n:::\\n\\n### \ubc31\ud654\uc810\uc774 \uc131\uc9c0\\n\\n\uc6b0\ub9ac\ub098\ub77c\ub294 \ubc31\ud654\uc810 \uc640\uc778\uc740 \ubcf4\ud1b5 \ube44\uc2f8\ub2e4. (\ubb3c\ub860, [\uc544\ub2cc \uacbd\uc6b0](https://goo.gl/maps/GNrMgDsMRfcExmJo9?coh=178573&entry=tt)\ub3c4 \uc5ec\ub7ff \uc788\uc5c8\uc74c)\\n\\n\ud558\uc9c0\ub9cc \uc77c\ubcf8\uc740 \ubc31\ud654\uc810\uc774 \uc640\uc778 \uc131\uc9c0\ub2e4. \uc720\uba85 \uc0dd\uc0b0\uc790\uc758 \uc640\uc778\ub4e4\uc774 \ud55c \uacf3\uc5d0 \ubaa8\uc5ec\uc788\uace0, \uad00\uad11\uac1d \ub300\uc0c1 \ud560\uc778\ud589\uc0ac\ub3c4 \uc790\uc8fc\uc788\uace0 \uba74\uc138 \ud61c\ud0dd\ubc1b\uae30\ub3c4 \uc27d\ub2e4.\\n\\n:::info \uc624\uc0ac\uce74 \uc8fc\uc694 \ubc31\ud654\uc810\\n\\n- [\ub2e4\uce74\uc2dc\ub9c8\uc57c \ubc31\ud654\uc810](https://goo.gl/maps/pgRSeL7uGQ6df5jX6?coh=178573&entry=tt) B1 \uc640\uc778\ucf54\ub108 : \ub09c\uce74\uc774\ub09c\ubc14\uc5ed\uc5d0 \uc704\uce58\ud55c \ubc31\ud654\uc810. \uc624\uc0ac\uce74 \uc5ec\ub7ec \ubc31\ud654\uc810 \uc911 \uc640\uc778 \ub9ac\uc2a4\ud2b8, \uac00\uaca9\uc774 \ucd5c\uace0\uc600\ub2e4.\\n- [\uae34\ud14c\uc4f0 \ubc31\ud654\uc810](https://goo.gl/maps/xCBW3FThrg5KWvpv6?coh=178573&entry=tt) B1 \uc640\uc778\ucf54\ub108 : \ub374\ub178\uc9c0\uc5ed\uc5d0 \uc704\uce58\ud55c \ubc31\ud654\uc810. \ud558\ub8e8\uce74\uc2a4 \ube4c\ub529 \uc548\uc5d0 \uc788\ub2e4. \uac00\ubcf4\uc9c4 \ubabb\ud588\uc9c0\ub9cc, \uc5ec\uae34 \uace0\uac00 \uc640\uc778\uc774 \ub9ce\ub2e4\uace0 \ud568.\\n\\n:::\\n\\n### \uc8fc\ub958 \uc804\ubb38\uc810\\n\\n\uc640\uc778 \uc804\ubb38\uc810\ub3c4 \ub9ce\ub2e4. \ub370\uc77c\ub9ac \uc640\uc778\uc744 \uad6c\uc785\ud558\uae30\uc5d0\ub294 \ubc31\ud654\uc810\ubcf4\ub2e4 \ub098\uc744 \uc218 \uc788\uaca0\ub2e4. \uc2dc\uac04\uc774 \uc5c6\uc5b4\uc11c \uc9c1\uc811 \uac00\ubcf4\uc9c4 \ubabb\ud588\ub2e4 \ud83e\udd72\\n\\n:::info \uc8fc\uc694 \uc8fc\ub958 \uc804\ubb38\uc810\\n\\n- [\ud0c0\uce74\ubb34\ub77c](https://goo.gl/maps/bGqrZWFv6JoDcJcz8?coh=178573&entry=tt) : \uc640\uc778, \uc0ac\ucf00, \uc704\uc2a4\ud0a4\uc640 \ub354\ubd88\uc5b4 \ucee4\ud53c \uc804\ubb38\uc810\uacfc \ub808\uc2a4\ud1a0\ub791\uc774 \ud568\uaed8 \uc788\ub2e4. \uad00\uad11\uc9c0\uc640 \uc9c0\ud558\ucca0\uc5ed\uacfc \uba40\uc5b4\uc11c \ucc38 \uc560\ub9e4\ud558\ub2e4. \uc640\uc778 \uc88b\uc544\ud558\ub294 \ubd84\ub4e4 \ub9d0\ub85c\ub294, \uad73\uc774 \uac00\ubcfc \ud544\uc694\ub294 \uc5c6\ub2e4\uace0 \ud55c\ub2e4.\\n- [Liquor Mountain](https://goo.gl/maps/M5fAU2x4yYKLs3Jz5?coh=178573&entry=tt) : \uccb4\uc778\uc810\uc774\ub77c\uc11c \uc77c\ubcf8 \uc804\uc5ed\uc5d0 \ub9ce\ub2e4. \uc624\uc0ac\uce74\uc5d0\ub3c4 \uc5ec\ub7ec \uacf3\uc774 \uc788\ub2e4. \uc6b0\uba54\ub2e4\uc5d0\uc11c \ubc29\ubb38\ud558\ub824\ub2e4\uac00 \uc2dc\uac04\uc774 \uc5c6\uc5b4\uc11c \ubabb\uac14\ub2e4.\\n\\n:::\\n\\n## \ub2e4\uce74\uc2dc\uc57c\ub9c8 \ubc31\ud654\uc810 \uc9e7\uc740 \ud6c4\uae30 \ud83d\udcdd\\n\\n\uc624\uc0ac\uce74 \uc219\uc18c\uac00 \ub2e4\uce74\uc2dc\uc57c\ub9c8 \uc778\uadfc\uc774\ub77c\uc11c, 2\ubc88 \ubc29\ubb38\ud588\ub2e4.\\n\uc624\uc0ac\uce74 \uc624\ub294 \uc0ac\ub78c\uc774\ub77c\uba74 \ub204\uad6c\ub098 \uc9c0\ub098\ub294 \ub09c\uce74\uc774\ub09c\ubc14\uc5ed\uc5d0 \uc704\uce58\ud574 \uc788\uc5b4\uc11c, \uc624\uc0ac\uce74 \uc5ec\ud589 \uc911 \uc2dc\uac04\uc774 \uc5c6\ub2e4\uba74 \uc5ec\uae30\ub9cc \uac00\ub3c4 \ucda9\ubd84\ud560 \uac83 \uac19\ub2e4.\\n\\n\uccab\ub0a0\uc740 \ubb34\ub8cc \uc2dc\uc74c\ud589\uc0ac\ub3c4 \ud558\uace0 \uc788\uc5c8\ub2e4! **Josmeyer Le Kottabe Riesling**\uc774\uc600\ub294\ub370 \ub108\ubb34 \ub9db\uc788\uc5b4\uc11c \uad6c\uc785\ud588\ub2e4. 4,700\uc5d4 \uc815\ub3c4. \ud55c\uad6d\uc5d0\uc11c\ub294 \uc5bc\ub9c8\uc778\uc9c0\ub294 \ubaa8\ub974\uaca0\ub2e4 \ud83d\ude05 (\uc0ac\uc2e4 \uac70\uc758 \ubcf8\uc801\uc774 \uc5c6\ub2e4)\\n\\n[![Josmeyer](./images/1.jpg)](./images/1.jpg)\\n[![Guffens-Heynen](./images/2.jpg)](./images/2.jpg)\\n[![Philipe-Pacalet](./images/3.jpg)](./images/3.jpg)\\n\\n\ud6c4\ud68c\ub418\ub294\uac74 \uc774\ub0a0 \uadf8\ub0e5 **Guffens-Heynen Saint-V\xe9ran \'1er Jus\'**\ub791 **Philipe Pacalet**\ub97c \uad6c\uc785\ud560\uac78 \uadf8\ub7ac\ub2e4\ub294 \uac83. \uadf8\uc678\uc5d0\ub3c4 \ucc28\ub77c\ub9ac \ubd80\ub974\uace0\ub274 \ub808\ub4dc\ub97c \uc0ac\uba74 \uc5b4\ub560\uc744\uae4c \uc2f6\ub2e4! \ub2e4\uc74c\uc5d0 \ub610 \uac08 \uba85\ubd84\uc774 \uc0dd\uacbc\ub2e4 \ud83d\ude02\\n\\n\ub450\ubc88\uc9f8 \ubc29\ubb38\ub54c **Guffens-Heynen Saint-V\xe9ran \'1er Jus\'**\ub294 \ud314\ub9ac\uace0 \uc5c6\uc5c8\ub2e4. \uc544\uc26c\uc6b4\ub300\ub85c **Villages**\uae09\uc73c\ub85c \ub9cc\uc871! \uadf8\uc640 \ub354\ubd88\uc5b4 \uacc4\uc18d \uc5fc\ub450\uc5d0 \ub450\uace0 \uc788\uc5c8\ub358 **Josmeyer Riesling Grand Cru Hengst**\ub3c4 \uad6c\uc785\ud588\ub2e4.\\n\\n[![Wines](./images/4.jpg)](./images/4.jpg)\\n\\n\uac00\uaca9\ud45c\uc5d0 \uad75\uace0 \ud070 \uae00\uc528 \uc606\uc5d0 \uc791\uc740 \uae00\uc528\ub85c \uc801\ud78c\uac8c \uba74\uc138 \uac00\uaca9\uc774\ub2e4. 7\uce35\uc5d0 \uba74\uc138\ucf54\ub108\uac00 \uc788\uc5b4\uc11c \ubc14\ub85c \ud658\uae09\ubc1b\uc744 \uc218 \uc788\ub2e4. \uccb4\ud06c\uce74\ub4dc\ub85c \uacb0\uc81c\ud574\uc11c \ud604\uae08\uc73c\ub85c \ud658\uae09\ubc1b\uc558\ub2e4.\\n\\n\uce90\ub9ac\uc5b4\uc5d0 \ub123\uace0 \ube44\ud589\uae30\ub97c \ud0c8 \uc608\uc815\uc774\ub77c\uace0 \ud558\ub2c8, \ud3ec\uc7a5\ub3c4 \uc5c4\uccad \uaf3c\uaf3c\ud558\uac8c \ud574\uc92c\ub2e4. \ubb3c\ub860, \ubb34\ub8cc!\\n\\n[![Packing](./images/5.jpg)](./images/5.jpg)\\n\\n\uc5ec\ud589 \ud6c4\uae30\ub294 \uc5b8\uc81c\uc4f4\ub2f4 \ud83e\udd23"},{"id":"2023-04-24-daily","metadata":{"permalink":"/daily/2023-04-24-daily","source":"@site/daily/2023-04-24-daily/index.md","title":"230424 \uc0dd\uac01\uc815\ub9ac \ud83d\ude44","description":"\ubb38\ub4dd \uc0dd\uac01\ub09c \uac83\ub4e4\uc744 \uc815\ub9ac\ud574\ubcf4\uc558\ub2e4.","date":"2023-04-24T00:00:00.000Z","formattedDate":"2023\ub144 4\uc6d4 24\uc77c","tags":[{"label":"thinking","permalink":"/daily/tags/thinking"},{"label":"travel","permalink":"/daily/tags/travel"}],"readingTime":2.7,"hasTruncateMarker":true,"authors":[{"name":"\uc2ec\uc644","title":"\ud504\ub860\ud2b8\uc5d4\ub4dc \uac1c\ubc1c\uc790 @ HandHug","url":"https://github.com/0420syj","imageURL":"https://avatars.githubusercontent.com/u/22449484","key":"wannysim"}],"frontMatter":{"slug":"2023-04-24-daily","title":"230424 \uc0dd\uac01\uc815\ub9ac \ud83d\ude44","description":"\ubb38\ub4dd \uc0dd\uac01\ub09c \uac83\ub4e4\uc744 \uc815\ub9ac\ud574\ubcf4\uc558\ub2e4.","image":"./images/calc.png","authors":["wannysim"],"tags":["thinking","travel"],"enableComments":true},"prevItem":{"title":"\uc624\uc0ac\uce74\uc5d0\uc11c \uc640\uc778 \uad6c\uc785\ud558\uae30 \ud83c\udf77","permalink":"/daily/2023-05-11-japan-wine"},"nextItem":{"title":"\uccab \ud3ec\uc2a4\ud2b8\ub294 \uace0\uc591\uc774 \ud83d\udc31","permalink":"/daily/first-daily"}},"content":"\ubb38\ub4dd \uc0dd\uac01\ub09c \uac83\ub4e4\uc744 \uc815\ub9ac\ud574\ubcf4\uc558\ub2e4.\\n\\n\x3c!-- truncate --\x3e\\n\\n## \ubd80\uc815\uc801\uc778 \uac10\uc815\uc5d0\uc11c \ubc97\uc5b4\ub098\ub294 \ubc29\ubc95 \ud83e\udde0\\n\\n[LinkedIn](https://www.linkedin.com/posts/stevenouri_if-you-are-stuck-with-a-problem-or-cant-activity-7055883225419694080-9WAw?utm_source=share&utm_medium=member_desktop)\uc5d0\uc11c \uc7ac\ubc0c\ub294 \uae00\uc744 \uc77d\uc5c8\ub2e4.\\n\\n\ub1cc\uacfc\ud559 \uad00\ub828 [\ub17c\ubb38](https://www.sciencedirect.com/science/article/pii/S0306452209001171)\uc744 \uc77d\uace0 \uc815\ub9ac\ud55c \uae00\uc778\ub370, \ub300\ucda9 *\uac77\uae30\uc640 \uac19\uc740 \uc911\uac04 \uac15\ub3c4\uc758 \uc720\uc0b0\uc18c \uc6b4\ub3d9\uc774 \uc0ac\ucd98\uae30 \uc804 \uc544\ub3d9\uc758 \uc8fc\uc758\ub825 \uc870\uc808 \ub2a5\ub825 \ud5a5\uc0c1\uc5d0 \ub3c4\uc6c0\uc774 \ub41c\ub2e4*\ub77c\ub294 \ub0b4\uc6a9\uc774\ub2e4.\\n\\n\uadf8\uac83\ubcf4\ub2e4 \ub354 \uc7ac\ubc0c\ub294 \uac83\uc740, \uae00\uc758 \ub9c8\uc9c0\ub9c9\uc5d0 \uc788\ub294 \ubd80\ubd84\uc774\ub2e4.\\n\\n:::info \ubd80\uc815\uc801\uc778 \uac10\uc815\uc5d0\uc11c \ubc97\uc5b4\ub098\ub294 \ubc29\ubc95\\n\\n- Tired \u27a1\ufe0f Nap\\n- Sad \u27a1\ufe0f Music\\n- Cant focus\u27a1\ufe0f Cut screen time\\n- Angry \u27a1\ufe0f Exercise\\n- Burnt out \u27a1\ufe0f Read\\n- Anxious \u27a1\ufe0f Meditate\\n- Feeling lost \u27a1\ufe0f Pray\\n- Overwhelmed \u27a1\ufe0f Take a break\\n- Restless \u27a1\ufe0f Yoga\\n- Nervous \u27a1\ufe0f Deep breaths\\n- Unmotivated \u27a1\ufe0f Set small goals\\n\\n:::\\n\\n\ub300\ubd80\ubd84\uc740 \uacf5\uac10\ub418\ub294 \ub0b4\uc6a9\uc774\uc5c8\ub2e4. \uc77c\ubd80\ub294 \ud574\uc11d \uc790\uccb4\uac00 \uc5b4\ub824\uc6e0\ub2e4.\\n\\n\uc5b4\uca0b\ub4e0 \ubd80\uc815\uc801\uc778 \uac10\uc815\uc774 \ub4e4 \ub54c, \uc774 \uae00\uc744 \ubcf4\uace0 \uc5b4\ub5a4 \uac83\uc744 \ud574\uc57c\ud560\uc9c0 \uace0\ubbfc\ud574\ubcf4\uae30\ub85c \ud558\uc790.\\n\\n## \uc77c\ubcf8 \uc5ec\ud589 \uc900\ube44 \ud83d\uddd2\ufe0f\\n\\n\uc774\uc9c1 \uc804 \ud734\uac00\uae30\uac04\uc774 \ub9ce\uc774 \uc0dd\uaca8\uc11c \uc77c\ubcf8 \uc5ec\ud589\uc744 \uacc4\ud68d\ud558\uace0 \uc788\ub2e4. \ub2f9\uc7a5 \uc774\ubc88 \uc8fc \ubaa9\uc694\uc77c\uc774\ub2e4.\\n\\n\uc695\uc2ec\uc744 \ub9ce\uc774 \ub0c8\ub354\ub2c8 \uba38\ub9ac \uc544\ud504\ub2e4. \uc77c\ubcf8\uc5d0 \ub300\ud55c \uc9c0\uc2dd\uc774 \ubd80\uc871\ud574\uc11c \ub354 \uadf8\ub7f0 \uac83 \uac19\ub2e4. \uac00\uc788\ub294 \ub3d9\uc548\uc5d0\ub3c4 \ube14\ub85c\uae45\ud574\uc57c\uaca0\ub2e4!\\n\\n## \uc5ec\ud589 \uc6f9\uc571\uc5d0 \ub300\ud55c \uace0\ubbfc \ud83e\udd14\\n\\n\uc9c0\ub09c 12\uc6d4 \uc7ac\ubbf8\ub85c \uc5ec\ud589\uc5d0 \ub3c4\uc6c0\uc774 \ub418\ub294 \uc6f9\uc571\uc744 \ub9cc\ub4e4\uae30 \uc2dc\uc791\ud588\ub2e4. \ub098\uc5d0\uac8c \uc774\uc9c1\uc758 \uacc4\uae30\ub97c \ub9cc\ub4e4\uc5b4\uc900 \uc5b4\ud50c\ub9ac\ucf00\uc774\uc158\uc774\ub2e4.\\n\\n![\ucea1\ucc98 \uc774\ubbf8\uc9c0](./images/calc.png)\\n\\n:::note \ub300\ud45c \uae30\ub2a5\\n\\n- \ud658\uc728 \uacc4\uc0b0\uae30\\n- \uc5ec\ud589 \ud68c\ud654\ubb38\uc7a5\\n- \uae30\uc0c1\uccad \ub9c1\ud06c\\n- \uc5ec\ud589\uc9c0 LiveCam \ub9c1\ud06c\\n- \ub4f1\ub4f1...\\n\\n:::\\n\\n\uc720\ub7fd\ub54c \ub123\uc5c8\ub358 \ub370\uc774\ud130\ub97c \uc77c\ubcf8 \uc624\uc0ac\uce74&\ub3c4\ucfc4\ub85c \ubc14\uafe8\ub2e4.\\n\\n\uadf8\ub7f0\ub370 \uc815\ub9d0 \ub300\ucda9 \ub9cc\ub4e4\uc5b4\uc11c \ud655\uc7a5\uc131\uc774 \ub108\ubb34 \ub5a8\uc5b4\uc9c4\ub2e4. \uc81c\ubc95 \uad1c\ucc2e\uc740 \uae30\ub2a5\uc774 \ub9ce\uc774 \uc788\uc5b4\uc11c \uc544\ud0a4\ud14d\ucc98\ub97c \uac1c\uc120\ud558\uba74 \uc55e\uc73c\ub85c\ub3c4 \ub9ce\uc774 \uc4f8 \uc218 \uc788\uc744 \uac83 \uac19\uc740\ub370 \ub9d0\uc774\ub2e4.\\n\\n\uae30\ud68c\uac00 \ub418\uba74 \ub9ac\ud329\ud1a0\ub9c1\uc744 \ud574\ubcf4\uace0 public repo\ub85c \uc62c\ub824\uc57c\uaca0\ub2e4. (\uc9c0\uae08\uc740 private\uc774\ub2e4\ud83d\ude05)"},{"id":"first-daily","metadata":{"permalink":"/daily/first-daily","source":"@site/daily/2023-04-22-first-daily/index.md","title":"\uccab \ud3ec\uc2a4\ud2b8\ub294 \uace0\uc591\uc774 \ud83d\udc31","description":"\uc77c\uc0c1\uc801\uc778 \ub0b4\uc6a9\uc744 \uae30\ub85d\ud558\ub294 \uacf5\uac04\uc744 \ub9cc\ub4e4\uc5c8\uc2b5\ub2c8\ub2e4. \uc624\ub298\uc758 \uc8fc\uc81c\ub294 \uace0\uc591\uc774\uc785\ub2c8\ub2e4.","date":"2023-04-22T00:00:00.000Z","formattedDate":"2023\ub144 4\uc6d4 22\uc77c","tags":[{"label":"cat","permalink":"/daily/tags/cat"}],"readingTime":1.43,"hasTruncateMarker":true,"authors":[{"name":"\uc2ec\uc644","title":"\ud504\ub860\ud2b8\uc5d4\ub4dc \uac1c\ubc1c\uc790 @ HandHug","url":"https://github.com/0420syj","imageURL":"https://avatars.githubusercontent.com/u/22449484","key":"wannysim"}],"frontMatter":{"slug":"first-daily","title":"\uccab \ud3ec\uc2a4\ud2b8\ub294 \uace0\uc591\uc774 \ud83d\udc31","description":"\uc77c\uc0c1\uc801\uc778 \ub0b4\uc6a9\uc744 \uae30\ub85d\ud558\ub294 \uacf5\uac04\uc744 \ub9cc\ub4e4\uc5c8\uc2b5\ub2c8\ub2e4. \uc624\ub298\uc758 \uc8fc\uc81c\ub294 \uace0\uc591\uc774\uc785\ub2c8\ub2e4.","image":"./images/maru-aki.jpg","authors":["wannysim"],"tags":["cat"],"enableComments":true},"prevItem":{"title":"230424 \uc0dd\uac01\uc815\ub9ac \ud83d\ude44","permalink":"/daily/2023-04-24-daily"}},"content":"\uc77c\uc0c1\uc801\uc778 \ub0b4\uc6a9\uc744 \uae30\ub85d\ud558\ub294 \uacf5\uac04\uc744 \ub9cc\ub4e4\uc5c8\uc2b5\ub2c8\ub2e4. \uc624\ub298\uc758 \uc8fc\uc81c\ub294 \uace0\uc591\uc774\uc785\ub2c8\ub2e4.\\n\\n\x3c!-- truncate --\x3e\\n\\n## \ubb34\uc2a8 \ub0b4\uc6a9\uc744 \uc4f0\uba74 \uc88b\uc744\uae4c\uc694? \ud83e\udd14\\n\\n\uc77c\uae30\uc7a5\ubcf4\ub2e4\ub294 \uc870\uae08 public\ud55c \ub290\ub08c\uc73c\ub85c \uc4f0\uace0 \uc2f6\uc5b4\uc11c, \ube14\ub85c\uadf8\uc5d0 **\uc77c\uc0c1** \uce74\ud14c\uace0\ub9ac\ub97c \ub9cc\ub4e4\uc5c8\uc2b5\ub2c8\ub2e4.\\n\\n\uc624\ub298\uc740 \uadf8\ub0e5 \uc800\uc758 \uace0\uc591\uc774\ub2d8 \ub450 \ubd84\uc744 \uc18c\uac1c\ud574\ub4dc\ub9b4\uae4c \ud569\ub2c8\ub2e4.\\n\\n## \uba38\ub8e8 \ud83d\udc31\\n\\n![\uba38\ub8e8](./images/maru.jpg)\\n\\n\uc0ac\ub0e5\ub180\uc774\ub97c \uad49\uc7a5\ud788 \uc88b\uc544\ud558\ub294 **\uba38\ub8e8**\uc785\ub2c8\ub2e4.\\n\uac00\uc7a5 \uc88b\uc544\ud558\ub294 \uc6f9\ud230\uc778 [\ud658\uc0dd\ub3d9\ubb3c\ud559\uad50](https://series.naver.com/comic/detail.series?productNo=3050030)\uc758 \uce90\ub9ad\ud130 [\uba38\ub8e8](https://namu.wiki/w/%ED%99%98%EC%83%9D%EB%8F%99%EB%AC%BC%ED%95%99%EA%B5%90/%EB%93%B1%EC%9E%A5%EC%9D%B8%EB%AC%BC#s-1.2.1)\uc5d0\uc11c \ub530\uc654\uc2b5\ub2c8\ub2e4.\\n\\n\uc9c0\uae08\ub3c4 \ub180\uc544\ub2ec\ub77c\uace0 \uc2dc\uc704\uc911\uc774\uc5d0\uc694.\\n\\n## \uc544\ud0a4 \ud83d\udc31\\n\\n![\uc544\ud0a4](./images/aki.jpg)\\n\\n\uc560\uad50\uac00 \ub118\uce58\ub294 **\uc544\ud0a4**\uc785\ub2c8\ub2e4.\\n\ub9c8\ucc2c\uac00\uc9c0\ub85c \uc6f9\ud230 [\ud658\uc0dd\ub3d9\ubb3c\ud559\uad50](https://series.naver.com/comic/detail.series?productNo=3050030)\uc758 \uce90\ub9ad\ud130 [\uc544\ud0a4](https://namu.wiki/w/%ED%99%98%EC%83%9D%EB%8F%99%EB%AC%BC%ED%95%99%EA%B5%90/%EB%93%B1%EC%9E%A5%EC%9D%B8%EB%AC%BC#s-1.2.5)\uc5d0\uc11c \ub530\uc654\uc2b5\ub2c8\ub2e4.\\n\\n\ucef4\ud4e8\ud130 \uc55e\uc5d0 \uc788\uc73c\uba74, \uc4f0\ub2e4\ub4ec\uc5b4\ub2ec\ub77c\uace0 \uba38\ub9ac\ub97c \ub4e4\uc774\ub300\ub294 \ud3b8\uc785\ub2c8\ub2e4.\\n\\n## \uc0ac\uc774\uac00 \ucc38 \uc88b\uc544\uc694 \ud83e\udd17\\n\\n![\uba38\ub8e8\uc640 \uc544\ud0a4](./images/maru-aki.jpg)\\n\\n\uba38\ub8e8\uc640 \uc544\ud0a4\ub294 \uc0ac\uc774\uac00 \ucc38 \uc88b\uc544\uc694.\\n\uc11c\uc5f4\uc740 \uba38\ub8e8\uac00 \ub354 \ub192\uc2b5\ub2c8\ub2e4 \ud83d\ude0e"}]}')}}]);